import React from 'react';
import './MyComponent.css'; // Import CSS file for MyComponent

class MyComponent extends React.Component {
   state = {
       name: "Aegon Targaryen",
       age: 21,
       address: "King's Landing"
   };
    handleClick(event){
        console.log(" Tran Thao Nguyen is the most pretty girl in the world !");
        console.log(event);
    }
    handleOnMveOver(event){
        console.log(event.pageX)
    }
   render() {
       return (
           <div className="myComponent"> {/* Gắn lớp CSS là myComponent */}
               <h1>HOUSE OF THE DRAGON</h1>
               <p>
                   Lord of Winterfell is {this.state.name} ruling in {this.state.address}.
                   He is {this.state.age} years old.
                   <button onClick={this.handleOnMveOver}>move me</button>
                   <button onClick={(event)=>{this.handleClick(event)}}>click me</button>

               </p>
           </div>
       );
   }
}

export default MyComponent;
