import React from 'react';
import './MyComponent.css'; // Import CSS file for MyComponent
import UserIn4 from './UserIn4';
class MyComponent extends React.Component {
   state = {
       name: "Aegon Targaryen",
       age: 21,
       address: "King's Landing"
   };
    handleClick(event){
        console.log(" Tran Thao Nguyen is the most pretty girl in",this.state.address);
        console.log(event);
        this.setState({name:"daemon tagaryen"})
    }
    handleOnMveOver(event){
        console.log(event.pageX)
    }
    handleOnchangeInput = (event) => {
        this.setState({
            name: event.target.value,
            address:"dragon stone"
        })
    }
    handleOnchangeAge = (event) => {
        this.setState({
            age: event.target.value
        })
    }
    handleOnSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
    }
   render() {
       return (
           <div className="myComponent"> {/* Gắn lớp CSS là myComponent */}
               <h1>HOUSE OF THE DRAGON</h1>
                   <p>Lord of Winterfell is {this.state.name} ruling in {this.state.address}.
                   He is {this.state.age} years old.</p>
                    <form onSubmit={(event)=>this.handleOnSubmit(event)}>
                    <label>your name : </label>
                        <input 
                        value = {this.state.name}
                        type = "text"
                        onChange={(event)=>this.handleOnchangeInput(event)}/>

                    <label>your age : </label>
                        <input 
                        value = {this.state.age}
                        type = "number"
                        onChange={(event)=>this.handleOnchangeAge(event)}/>
                        <button>submit</button>
                    </form>
                    <UserIn4/>
           </div>
       );
   }
}

export default MyComponent;
